ction"},{"inputs":[],"name":"getSupportedFtsos","outputs":[{"internalType":"contract IIFtso[]","name":"_ftsos","type":"address[]"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getSupportedIndices","outputs":[{"internalType":"uint256[]","name":"_supportedIndices","type":"uint256[]"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getSupportedIndicesAndFtsos","outputs":[{"internalType":"uint256[]","name":"_supportedIndices","type":"uint256[]"},{"internalType":"contract IIFtso[]","name":"_ftsos","type":"address[]"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getSupportedIndicesAndSymbols","outputs":[{"internalType":"uint256[]","name":"_supportedIndices","type":"uint256[]"},{"internalType":"string[]","name":"_supportedSymbols","type":"string[]"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getSupportedIndicesSymbolsAndFtsos","outputs":[{"internalType":"uint256[]","name":"_supportedIndices","type":"uint256[]"},{"internalType":"string[]","name":"_supportedSymbols","type":"string[]"},{"internalType":"contract IIFtso[]","name":"_ftsos","type":"address[]"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getSupportedSymbols","outputs":[{"internalType":"string[]","name":"","type":"string[]"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getSupportedSymbolsAndFtsos","outputs":[{"internalType":"string[]","name":"_supportedSymbols","type":"string[]"},{"internalType":"contract IIFtso[]","name":"_ftsos","type":"address[]"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"string","name":"symbol","type":"string"},{"internalType":"uint256","name":"price","type":"uint256"}],"name":"setPrice","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"string[]","name":"_symbols","type":"string[]"}],"name":"setSupportedSymbols","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType"